<template>
  <div class="list" v-if="type">
    <template v-if="type === 'img'">
      <!--<img class="item" v-for="item of resourceList" :src="item" />-->
      <div class="item" v-for="item of resourceList">
        <a-image
          :height="height"
          :width="width"
          :src="item + '&type=3'"
          fallback="data:image/png;base64,"
        />
      </div>
    </template>
    <template v-if="type === 'audio'">
      <Icon
        icon="ant-design:play-circle-filled"
        v-for="(item, idx) in resourceList"
        :key="idx"
        :src="item"
        @click="playerAudio(item)"
      />
    </template>
    <template v-if="type === 'video'">
      <video
        class="item"
        :style="{ height }"
        v-for="(item, idx) in resourceList"
        :key="idx"
        :src="item"
      ></video>
    </template>
    <a-modal v-model:visible="audioModal.visible" :title="audioModal.title">
      <audio :src="audioModal.url" autoplay></audio>
    </a-modal>
  </div>
</template>

<script>
  import Icon from '/@/components/Icon'

  const imgResrouceExts = ['jpg', 'png', 'jpeg', 'gif', 'webp']
  const videoResourceExts = ['mp4']
  const audioResourceExts = ['mp3']
  export default {
    name: 'FileUrlPrase',
    components: { Icon },
    props: {
      fileUrl: {
        type: String,
      },
      width: {
        type: String,
        default: '20px',
      },
      height: {
        type: String,
        default: '40px',
      },
    },
    data() {
      return {
        type: '',
        resourceList: [],
        audioModal: {
          visible: false,
          title: '音频播放',
          url: '',
        },
      }
    },
    watch: {
      fileUrl() {
        this.calcHandler()
      },
    },
    created() {
      this.calcHandler()
    },
    methods: {
      calcHandler() {
        if (!this.fileUrl) {
		  // console.warn('fileUrl不能为空')
          return
        }
        if (this.fileUrl.indexOf(',') > -1) {
          this.type = 'img'
          this.resourceList = this.fileUrl.split(',')
          console.log(this.resourceList)
          return
        }
        const extString = this.fileUrl.split('.').pop().toLowerCase()
        let resourceList = []
        if (imgResrouceExts.includes(extString)) {
          this.type = 'img'
          resourceList = this.fileUrl.split('|')
        } else if (videoResourceExts.includes(extString)) {
          this.type = 'video'
          resourceList = this.fileUrl.split('|')
        } else if (audioResourceExts.includes(extString)) {
          this.type = 'audio'
          resourceList = this.fileUrl.split('|')
        } else {
          resourceList = this.fileUrl.split(',')
        }
        this.resourceList = resourceList
      },
      playerAudio(url) {
        if (!url) this.audioModal.url = url
        this.audioModal.visible = true
      },
    },
  }
</script>
<style>
  .list .item .ant-image-img {
    object-fit: contain !important;
  }
</style>
<style lang="scss" scoped>
  .list {
    .item {
      display: inline-block;
      //width: 40px;
      //height: 40px;
      margin-right: 8px;
    }
  }
</style>
